<?php/** * @package WordPress * @subpackage myTheme * The template for displaying Archive pages. */?><?php get_header(); ?><?php if (have_posts()) : ?>    <section class="clearfix col-xs-12 col-sm-12 col-md-8 col-lg-9" id="content">    <?php include TEMPLATEPATH . '/includes/breadcrumb.php'; ?>        <?php $post = $posts[0]; ?>        <?php if (is_category()) { ?>				            <h1 class="post-title hide"><?php echo single_cat_title(); ?></h1>        <?php } elseif (is_tag()) { ?>            <h1 class="post-title">Archive for the '<?php echo single_tag_title(); ?>' Tag</h1>        <?php } elseif (is_day()) { ?>            <h1 class="post-title">Archive for <?php the_time(get_option('date_format')) ?></h1>        <?php } elseif (is_month()) { ?>            <h1 class="post-title">Archive for <?php the_time('F, Y'); ?></h1>        <?php } elseif (is_year()) { ?>            <h1 class="post-title">Archive for <?php the_time('Y'); ?></h1>        <?php } elseif (is_search()) { ?>            <h1 class="post-title">Search Results</h1>        <?php } elseif (is_author()) { ?>            <h1 class="post-title">Author Archive</h1>        <?php } elseif (isset($_GET['paged']) && !empty($_GET['paged'])) { ?>            <h1 class="post-title">Blog Archives</h1>        <?php } ?>        <?php while (have_posts()) : the_post(); ?>            <div id="post-<?php the_ID(); ?>" <?php post_class('post_listing'); ?> >                <h2 class="post-title"><?php the_title(); ?></h2>                <div class="entry">                    <?php the_excerpt(); ?>                    <a href="<?php the_permalink() ?>" rel="bookmark" title="Permanent Link to <?php the_title(); ?>"><?php _e('Read more...'); ?></a>                </div>                <div class="clear"></div>            </div>        <?php endwhile; ?>        <div class="navigation">            <?php if (!function_exists('wp_pagenavi') || wp_pagenavi()): ?>                <div class="alignleft"><?php next_posts_link('&laquo; Older Entries') ?></div>                <div class="alignright"><?php previous_posts_link('Newer Entries &raquo;') ?></div>            <?php endif ?>        </div>    </section><?php else : ?>    <section id="content">        <h2 class="center">Not Found</h2>        <p class="center">Sorry, but you are looking for something that isn't here.</p>    </section><?php endif; ?><?php get_sidebar(); ?><?php get_footer(); ?>