<div id="banner" class="full_width">    <div class="container">        <div id="slideshow-wrapper" class="full_width">            <?php            $args = array('post_type' => 'slider',                'orderby ' => 'menu_order',                'showposts' => -1,                'order' => 'ASC');            $feature_banner = new WP_Query();            $feature_banner->query($args);            ?>            <?php if ($feature_banner->have_posts()) : ?>                <div id="home-slider" class="cycle-slideshow"                      data-cycle-slides="> div"                     data-cycle-fx=fade                     data-cycle-pager=".example-pager"                     data-cycle-pager-template="<a href='javascript:void(0);'> {{slideNum}} </a>"                     data-cycle-caption=".slider_label"                     data-cycle-caption-template={{cycleTitle}}                     data-cycle-prev=".sprev"                     data-cycle-next=".snext"                     data-cycle-speed=100                     data-cycle-timeout=10000 >                         <?php while ($feature_banner->have_posts()) : $feature_banner->the_post(); ?>                        <div class="slide-item" data-cycle-title='<?php if (get_field('photo_title')): ?><?php echo "Photo: " . get_field('photo_title'); ?><?php endif; ?>' >                            <a style="display:block;" href="<?php the_field('slider_link'); ?>">                                <?php the_post_thumbnail(); ?></a>                            <div class="slider_caption full_width ">                                <a style="display:block;" href="<?php the_field('slider_link'); ?>">                                <div class="full_width">                                    <div class="col-xs-12 col-sm-6 col-md-5 col-lg-5 pull-right scaption">                                                                                <h2 class="stitle"><?php the_title(); ?></h2>                                        <div class="hidden-xs"><?php the_content(); ?></div>                                        <!--<a class="more clearfix pull-right" href="<?php the_field('slider_link'); ?>"><?php _e('More...'); ?></a>-->                                                                            </div>                                </div>                            </a>                            </div>                        </div>                    <?php endwhile; ?>                </div>                <div class=" slider_controls">                    <div class="pull-left slider_label hidden-xs">                    </div>                    <div class="pull-right">                        <div class="example-pager"></div>                        <a class="scontrols sprev" href="javascript:void(0);"><?php _e('prev'); ?></a>                        <a class="scontrols snext" href="javascript:void(0);"><?php _e('next'); ?></a>                    </div>                </div>            <?php endif; ?>        </div>    </div></div>