<?php/**  Template Name: Blog */?><?php get_header(); ?><section class="clearfix col-xs-12 col-sm-12 col-md-8 col-lg-9" id="content">    <?php include TEMPLATEPATH . '/includes/breadcrumb.php'; ?>    <div class="subcontent">        <div id="post-<?php the_ID(); ?>" <?php post_class(); ?>>            <div class="entry">                <?php                $paged = ( get_query_var('paged') ) ? get_query_var('paged') : 1;                $args = array(                    'post_type' => 'blog',                    'posts_per_page' => 10,                    'paged' => $paged                    );                $feature_banner = new WP_Query();                $feature_banner->query($args);                ?>                <?php if ($feature_banner->have_posts()) : ?>                    <?php                    while ($feature_banner->have_posts()) : $feature_banner->the_post();                        ?>                        <div class="blog_item">                            <h3 class="catItemTitle"> <a href="<?php the_permalink() ?>"><?php the_title(); ?></a></h3>                            <div class="blog_meta"><?php _e('Written by'); ?> <?php /*the_author_posts_link(); */ ?><a href="<?php echo get_author_posts_url( get_the_author_meta( 'ID' ), get_the_author_meta( 'user_nicename' ) ); ?>"><?php the_author(); ?></a></div>                            <?php the_content(); ?>                            <div class="bottom_meta"><strong><?php _e('Published in') ?></strong> <?php echo get_custom_taxonomies($post, 'blog-category'); ?></div>                        </div>                        <?php                    endwhile;                    ?>                    <div class="navigation">                        <?php if (!function_exists('wp_pagenavi') || wp_pagenavi(array('query' => $feature_banner))): ?>                            <!--                            <div class="alignleft"><?php next_posts_link('&laquo; Older Entries') ?></div>                            <div class="alignright"><?php previous_posts_link('Newer Entries &raquo;') ?></div>                            -->                            <?php // next_posts_link() usage with max_num_pages ?>                            <div class="alignleft"><?php next_posts_link( '&laquo; Older Entries', $feature_banner->max_num_pages ); ?></div>                            <div class="alignright"><?php previous_posts_link( 'NNewer Entries &raquo;' ); ?></div>                        <?php endif ?>                    </div>                <?php endif; ?>                <div class="bottom_widget">                    <?php if (!function_exists('dynamic_sidebar') || !dynamic_sidebar('Sidebar-Bottom-Blog')) : ?>                    <?php endif; ?>                </div>            </div>				        </div>     </div></section><?php get_sidebar(); ?><?php get_footer(); ?>