<?php/** * @package WordPress * @subpackage myTheme * The Template for displaying all single posts. */?><?php get_header(); ?><?php if (have_posts()) : while (have_posts()) : the_post(); ?>        <section class="clearfix col-xs-12 col-sm-12 col-md-8 col-lg-9" id="content">            <?php include TEMPLATEPATH . '/includes/breadcrumb.php'; ?>            <h1 class="post-title"><?php the_title(); ?></h1>            <div class="blog_meta"><?php _e('Written by'); ?> <a href="<?php echo get_author_posts_url(get_the_author_meta('ID'), get_the_author_meta('user_nicename')); ?>"><?php the_author(); ?></a></div>             <div id="post-<?php the_ID(); ?>" <?php post_class(); ?>>                <div class="entry">                    <?php the_content(); ?>                </div>                <div class="clear"></div>                <div class="bottom_meta"><strong><?php _e('Published in') ?></strong> <?php echo get_custom_taxonomies($post, 'blog-category'); ?></div>                <?php echo get_related_author_posts(); ?>            </div>            <?php comments_template(); ?>        <?php endwhile; ?>        <div class="bottom_widget">            <?php if (!function_exists('dynamic_sidebar') || !dynamic_sidebar('Sidebar-Bottom-Blog')) : ?>            <?php endif; ?>        </div>    </section><?php else: ?>    <section id="content">        <h2 class="center">Not Found</h2>        <p class="center">Sorry, but you are looking for something that isn't here.</p>    </section><?php endif; ?><?php get_sidebar(); ?><?php get_footer(); ?>