<?php/** * @package WordPress * @subpackage myTheme * The Template for displaying all single posts. */?><?php get_header(); ?><?php if (have_posts()) : while (have_posts()) : the_post(); ?>        <section class="clearfix col-xs-12 col-sm-12 col-md-8 col-lg-9" id="content">            <?php include TEMPLATEPATH . '/includes/breadcrumb.php'; ?>            <h1 class="post-title"><?php the_title(); ?></h1>            <div id="post-<?php the_ID(); ?>" <?php post_class(); ?>>                <div class="entry">                    <?php the_content(); ?>                </div>                           <div class="clear"></div>                <p class="postmetadata"><?php the_tags(__('Tags:', 'myTheme') . ' ', ', ', ''); ?></p>            </div>            <?php //comments_template(); ?>        <?php endwhile; ?>    </section> <?php else: ?>    <section id="content">        <h2 class="center">Not Found</h2>        <p class="center">Sorry, but you are looking for something that isn't here.</p>    </section><?php endif; ?><?php get_sidebar(); ?> <?php get_footer(); ?>