<?php//General Admin Options$ds_prepare_theme_options = array(    array(        'type' => 'open'    ),    array(        'name' => 'General Options',        'type' => 'options_title'    ),     array(        'name' => 'Footer Copyright',        'desc' => '',        'id' => $ds_theme_key . 'footer_copyright',        'type' => 'text_area',        'default_value' => ''    ),    array(        'type' => 'close'    ));function ds_write_theme_options() {    global $ds_prepare_theme_options;    ?>    <div class="wrap">        <form method="post">            <?php            foreach ($ds_prepare_theme_options as $value) :                switch ($value['type']) :                    case 'open':                        ?><ul class="ds_admin">                            <?php                            if ($_REQUEST['reset'])                                echo '<li><div id="message" class="updated fade"><p><strong>Settings Reset.</strong></p></div></li>';                            if ($_REQUEST['saved'])                                echo '<li><div id="message" class="updated fade"><p><strong>Settings Saved.</strong></p></div></li>';                            ?>                            <?php                            break;                        case 'options_title':                            ?>                            <li>                                <div class="icon32" id="icon-options-general"><br/></div>                                <h2><?php echo $value['name']; ?></h2>                            </li>                            <?php                            break;                        case 'text_area':                            ?>                            <?php                            if (get_option($value['id']) == '') : update_option($value['id'], $value['default_value']);                            endif;                            ?>                            <li>                                <h3><?php echo $value['name']; ?></h3>                                <p><?php echo $value['desc']; ?></p>                                <?php                                                             $settings = array(                                    'teeny' => false,                                    'textarea_rows' => 15,                                    'tabindex' => 1                                );                                wp_editor(get_option($value['id'], $value['default_value']), $value['id'], $settings);                                                               ?>                            </li>                            <?php                            break;                        case 'type_currency':                            ?>                            <?php                            if (get_option($value['id']) == '') : update_option($value['id'], $value['default_value']);                            endif;                            ?>                            <li>                                <h3><?php echo $value['name']; ?></h3>                                <p><?php echo $value['desc']; ?></p>                                <p><input name="<?php echo $value['id']; ?>" id="<?php echo $value['id']; ?>" type="text" value="<?php echo htmlspecialchars(get_option($value['id'], $value['default_value'])); ?>" /></p>                            </li>                            <?php                            break;                        case 'type_blog_cat':                            ?>                            <?php                            if (get_option($value['id']) == '') : update_option($value['id'], $value['default_value']);                            endif;                            ?>                            <li>                                <h3><?php echo $value['name']; ?></h3>                                <p><?php echo $value['desc']; ?></p>                                <?php ds_get_category_list($value['id'], $value['default_value']); ?>                                <input name="<?php echo $value['id']; ?>" id="<?php echo $value['id']; ?>" type="hidden" value="<?php echo htmlspecialchars(get_option($value['id'], $value['default_value'])); ?>"/>                            </li>                            <?php                            break;                        case 'type_contact_us_id':                            ?>                            <?php                            if (get_option($value['id']) == '') : update_option($value['id'], $value['default_value']);                            endif;                            ?>                            <li>                                <h3><?php echo $value['name']; ?></h3>                                <p><?php echo $value['desc']; ?></p>                                <?php ds_get_page_list($value['id'], $value['default_value']); ?>                                <input name="<?php echo $value['id']; ?>" id="<?php echo $value['id']; ?>" type="hidden" value="<?php echo htmlspecialchars(get_option($value['id'], $value['default_value'])); ?>"/>                            </li>                            <?php                            break;                        case 'type_agent_parent_id':                            ?>                            <?php                            if (get_option($value['id']) == '') : update_option($value['id'], $value['default_value']);                            endif;                            ?>                            <li>                                <h3><?php echo $value['name']; ?></h3>                                <p><?php echo $value['desc']; ?></p>                                <?php ds_get_page_list($value['id'], $value['default_value']); ?>                                <input name="<?php echo $value['id']; ?>" id="<?php echo $value['id']; ?>" type="hidden" value="<?php echo htmlspecialchars(get_option($value['id'], $value['default_value'])); ?>"/>                            </li>                            <?php                            break;                        case 'number_thumbs':                            ?>                            <?php                            if (get_option($value['id']) == '') : update_option($value['id'], $value['default_value']);                            endif;                            ?>                            <li>                                <h3><?php echo $value['name']; ?></h3>                                <p><?php echo $value['desc']; ?></p>                                <p><input name="<?php echo $value['id']; ?>" id="<?php echo $value['id']; ?>" type="text" value="<?php echo htmlspecialchars(get_option($value['id'], $value['default_value'])); ?>" /></p>                            </li>                            <?php                            break;                        case 'type_recent_post_qty':                            ?>                            <?php                            if (get_option($value['id']) == '') : update_option($value['id'], $value['default_value']);                            endif;                            ?>                            <li>                                <h3><?php echo $value['name']; ?></h3>                                <p><?php echo $value['desc']; ?></p>                                <p><input name="<?php echo $value['id']; ?>" id="<?php echo $value['id']; ?>" type="text" value="<?php echo htmlspecialchars(get_option($value['id'], $value['default_value'])); ?>" /></p>                            </li>                            <?php                            break;                        case 'type_search_listing':                            ?>                            <?php                            if (get_option($value['id']) == '') : update_option($value['id'], $value['default_value']);                            endif;                            ?>                            <li>                                <h3><?php echo $value['name']; ?></h3>                                <?php                                echo '<input type="checkbox" value="check1" name="sample" id="cbutton1" onclick="document.getElementById(' . "'" . $value['id'] . "'" . ').value=this.checked;"';                                if (get_option($value['id']) == "true") {                                    echo ' checked="checked">';                                } else {                                    echo ' />';                                }                                echo ' <label for="cbutton1">' . $value['desc'] . '</label>';                                ?>                                <input name="<?php echo $value['id']; ?>" id="<?php echo $value['id']; ?>" type="hidden" value="<?php echo htmlspecialchars(get_option($value['id'], $value['default_value'])); ?>"/>                                <?php                                break;                            case 'type_location_exclude_categories':                                ?>                                <?php                                if (get_option($value['id']) == '') : update_option($value['id'], $value['default_value']);                                endif;                                ?>                            <li>                                <h3><?php echo $value['name']; ?></h3>                                <p><?php echo $value['desc']; ?></p>                                <p><input name="<?php echo $value['id']; ?>" id="<?php echo $value['id']; ?>" type="text" value="<?php echo htmlspecialchars(get_option($value['id'], $value['default_value'])); ?>" /></p>                            </li>                            <?php                            break;                        case 'facebook_id':                            ?>                            <li>                                <h3><?php echo $value['name']; ?></h3>                                <p><?php echo $value['desc']; ?></p>                                <p><label for="<?php echo $value['id']; ?>">Facebook ID </label><input name="<?php echo $value['id']; ?>" id="<?php echo $value['id']; ?>" type="text" value="<?php                                    if (get_option($value['id']) != "") {                                        echo get_option($value['id']);                                    } else {                                        echo $value['default_value'];                                    }                                    ?>" /></p>                            </li>                            <?php                            break;                        case 'type_google_analytics':                            ?>                            <?php                            if (get_option($value['id']) == '') : update_option($value['id'], $value['default_value']);                            endif;                            ?>                            <li>                                <h3><?php echo $value['name']; ?></h3>                                <p><?php echo $value['desc']; ?></p>                                <p><textarea name="<?php echo $value['id']; ?>" cols="55" rows="5"><?php echo htmlspecialchars(get_option($value['id'], $value['default_value'])); ?></textarea></p>                            </li>                            <?php                            break;                        case 'close':                            ?></ul><?php                        break;                    case 'type_google_map_api':                        ?>                        <?php                        if (get_option($value['id']) == '') : update_option($value['id'], $value['default_value']);                        endif;                        ?>                        <li>                            <h3><?php echo $value['name']; ?></h3>                            <p><?php echo $value['desc']; ?></p>                            <p><textarea name="<?php echo $value['id']; ?>" cols="55" rows="5"><?php echo htmlspecialchars(get_option($value['id'], $value['default_value'])); ?></textarea></p>                        </li>                        <?php                        break;                    case 'close':                        ?></ul><?php                        break;                endswitch;            endforeach;            ?>            <p class="submit" style="float: left;">                <input name="save" type="submit" value="Save changes" class="button-primary" />                <input type="hidden" name="action" value="save" />            </p>        </form>        <form method="post">            <p class="submit" style="float: left;">                <input name="reset" type="submit" value="Reset"/>                <input type="hidden" name="action" value="reset" />            </p>        </form>    </div>    <?php}function ds_put_theme_options_in_sidebar() {    global $ds_prepare_theme_options;    if ($_GET['page'] == 'functions.php') :        if ('save' == $_REQUEST['action']) :            foreach ($ds_prepare_theme_options as $value) :                update_option($value['id'], stripslashes_deep($_REQUEST[$value['id']]));            endforeach;            foreach ($ds_prepare_theme_options as $value) :                if (isset($_REQUEST[$value['id']])) :                    update_option($value['id'], stripslashes_deep($_REQUEST[$value['id']]));                else :                    delete_option($value['id']);                endif;            endforeach;            header('Location: admin.php?page=functions.php&saved=true');            die;        elseif ('reset' == $_REQUEST['action']) :            foreach ($ds_prepare_theme_options as $value) :                delete_option($value['id']);            endforeach;            header('Location: admin.php?page=functions.php&reset=true');            die;        endif;    endif;    add_menu_page('General Options', 'Theme Editor', 8, 'functions.php', 'ds_write_theme_options');    add_submenu_page('functions.php', 'General Options', 'General Options', 8, 'functions.php', 'ds_write_theme_options');}add_action('admin_menu', 'ds_put_theme_options_in_sidebar');?>